  margin-top: 0px;
}

#home {
  position: relative;
  overflow: hidden;
}

#home::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: url("/./assets/uiucpic.jpg");
  background-attachment: fixed;
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  filter: contrast(50%) saturate(110%);
  z-index: 1;
}

#home > * {
  position: relative;
  z-index: 2;
}
#about {
  background: #FF5F05;
  justify-content: space-around;
}
.about li {
  font-size: 1.5rem;
  color: white;
  font-family: "Open Sans", sans-serif;
  text-align: center;
  list-style-position: inside;
  margin-bottom: 5px;
}


footer {
    text-align: center;
    padding: 20px;
    background: #111;
    color: #eee;
    display:flex;
    justify-content: space-around;
}

foot {
    font-size: 12px;
    color: #fff;
}
footer i, 
footer a i {
  color: #fff !important;
}

/* Carousel Container */
.carousel {
  position: relative;
  width: 80%;
  max-width: 900px;
  overflow: hidden;
  border-radius: 15px;
  margin: 40px auto;
  box-shadow: 0 6px 18px rgba(0,0,0,0.25);
  background: #fff;
}

/* Viewport */
.carousel__viewport {
  display: flex;
  scroll-snap-type: x mandatory;
  overflow-x: scroll;
  scroll-behavior: smooth;
  -webkit-overflow-scrolling: touch;
  scrollbar-width: none; /* Firefox */
  -ms-overflow-style: none; /* IE 10+ */
}
.carousel__viewport::-webkit-scrollbar {
  display: none; /* Chrome, Safari, Opera */
}

/* Slides */
.carousel__slide {
  flex: 0 0 100%; /* each slide fills viewport */
  scroll-snap-align: center;
  position: relative;
  text-align: center;
}

.carousel__slide img {
  width: 100%;
  height: 400px;
  object-fit: cover;
  border-bottom: 4px solid #13294B;
}

.carousel__slide p {
  font-size: 1.5rem;
  padding: 10px;
  color: #13294B;
  font-weight: bold;
}

/* Controls */
.carousel__navigation {
  position: absolute;
  top: 50%;
  width: 100%;
  display: flex;
  justify-content: space-between;
  transform: translateY(-50%);
  pointer-events: none;
}

.carousel__navigation button {
  background: rgba(0,0,0,0.5);
  border: none;
  color: white;
  font-size: 2rem;
  padding: 10px 15px;
  border-radius: 50%;
  cursor: pointer;
  pointer-events: all;
  transition: background 0.3s ease;
  margin: 5px;
}

.carousel__navigation button:hover {
  background: #FF5F05;
}

/* Dot Navigation */
.carousel__dots {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 18px 0 10px 0;
  gap: 12px;
  position: relative;
  z-index: 2;
}
.carousel__dot {
  width: 18px;
  height: 18px;
  border-radius: 50%;
  background: #e0e0e0;
  border: 2px solid #FF5F05;
  display: inline-block;
  transition: background 0.3s, transform 0.3s, border 0.3s;
  cursor: pointer;
  box-shadow: 0 1px 4px rgba(0,0,0,0.10);
}
.carousel__dot.active {
  background: #FF5F05;
  transform: scale(1.2);
}
#favs {
  background: #FF5F05;
  width: 100%;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.favs-grid {
  display: grid;
  grid-template-columns: repeat(3, minmax(0, 1fr));
  gap: 1.2rem;
  width: 100vw;
  max-width: 1050px;
  margin: 0 auto;
  box-sizing: border-box;
}

.fav-card {
  background: #fff;
  border-radius: 1rem;
  overflow: hidden;
  box-shadow: 0 4px 8px rgba(0,0,0,0.1);
  cursor: pointer;
  transition: transform 0.2s ease;
  width: 90%;
  max-width: 220px;
  min-width: 0;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
}
.fav-card:hover {
  transform: translateY(-5px);
}
.fav-card img {
  width: 100%;
  height: 70px;
  object-fit: cover;
  display: block;

}
.fav-card .card-info {
  padding: 0.5rem;
  text-align: center;
}

@media (max-width: 900px) {
  .favs-grid {
    grid-template-columns: repeat(2, minmax(0, 1fr));
  }
}
@media (max-width: 600px) {
  .favs-grid {
    grid-template-columns: 1fr;
    gap: 1rem;

  }
  .fav-card {
    max-width: 95vw;
    width: 98%;

  }
}
  /* Modal */
  .modal {
    display: none; /* Hidden by default */
    position: fixed;
    z-index: 1000;
    inset: 0;
    background: rgba(0,0,0,0.6);
    justify-content: center;
    align-items: center;

  }

  .modal-content {
    background: #fff;
    border-radius: 1rem;
    padding: 1.5rem;
    max-width: 500px;
    width: 90%;
    text-align: center;

    img {
      width: 100%;
      border-radius: 0.5rem;
      margin-bottom: 1rem;
    }
  }

  .close {
    position: absolute;
    top: 1rem;
    right: 1.5rem;
    font-size: 1.5rem;
    cursor: pointer;
  }

