/* Carousel Container */
.carousel {
  position: relative;
  width: 80%;
  max-width: 900px;
  overflow: hidden;
  border-radius: 15px;
  margin: 40px auto;
  box-shadow: 0 6px 18px rgba(0,0,0,0.25);
  background: #fff;
}

/* Viewport */
.carousel__viewport {
  display: flex;
  scroll-snap-type: x mandatory;
  overflow-x: scroll;
  scroll-behavior: smooth;
  -webkit-overflow-scrolling: touch;
  scrollbar-width: none; /* Firefox */
  -ms-overflow-style: none; /* IE 10+ */
}
.carousel__viewport::-webkit-scrollbar {
  display: none; /* Chrome, Safari, Opera */
}

/* Slides */
.carousel__slide {
  flex: 0 0 100%; /* each slide fills viewport */
  scroll-snap-align: center;
  position: relative;
  text-align: center;
}

.carousel__slide img {
  width: 100%;
  height: 400px;
  object-fit: cover;
  border-bottom: 4px solid #13294B;
}

.carousel__slide p {
  font-size: 1.5rem;
  padding: 10px;
  color: #13294B;
  font-weight: bold;
}

