}


/* Navbar styles from main.scss */
#navbar {
  position: fixed;
  top: 0;
  width: 100%;
  height: 100px;
  background: #13294B;
  transition: all 0.3s ease;
  display: flex;
  justify-content: center; 
  align-items: center;
  padding: 0 40px;
  z-index: 999;
}

.nav-container {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
}

.nav-title {
  font-size: 32px;
  font-weight: bold;
  color: #fff;
  letter-spacing: 2px;
}

.nav-menu {
  display: flex;
  list-style: none;
  background: #e0e0e0;
  border-radius: 25px;
  padding: 8px 20px;
  margin-left: auto;
  margin-top: 10px;
  margin-bottom: 10px;
}

.nav-menu li {
  margin: 0 10px;
}

.nav-menu a {
  display: block;
  padding: 10px 15px;
  font-size: 18px;
  color: #333;
  text-decoration: none;
  border-radius: 15px;
  transition: background 0.3s, color 0.3s;
}

.nav-menu a:hover {
    background: #ffca7b;
    color: white;
}
.nav-menu a.active {
  background: #FF5F05;
  color: white;
}

#navbar.shrink {
    background: #13294B;
    height: 70px;
}
#navbar.shrink a {
    padding: 10px;
    font-size: 16px;
}

#navbar.shrink .nav-title {
    padding: 10px;
    font-size: 28px;
}

