    transition: background 0.3s, color 0.3s;
  }
  .nav-menu a:hover {
    background: #ffca7b;
    color: white;
  }
  .nav-menu a.active {
    background: #FF5F05;
    color: white;
  }
  #navbar.shrink {
    background: #13294B;
    height: 70px;
  }
  #navbar.shrink a {
    padding: 10px;
    font-size: 16px;
  }
  #navbar.shrink .nav-title {
    padding: 10px;
    font-size: 28px;
  }

  #home {
    position: relative;
    overflow: hidden;
  }
  #home::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url("/./assets/uiucpic.jpg");
    background-attachment: fixed;
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    filter: contrast(50%) saturate(110%);
    z-index: 1;
  }
  #home > * {
    position: relative;
    z-index: 2;
  }
  #home p {
    font-size: 1.5rem;
    color: white;
  }

  #about {
    background: #FF5F05;
    display: flex;
    flex-direction: row;
    align-items: stretch;
    justify-content: space-around;
    flex-wrap: wrap;
    width: 100%;
    box-sizing: border-box;
    gap: 0;
    padding: 20px;
  }
  #about h1 {
    color: #13294B;
    margin-bottom: 10px;
  }
  .about-text {
    margin: 0;
    flex: 1 1 320px;
    padding: 40px;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    align-items: center;
  }
  .about-text i.fa-heart {
    display: block;
    margin-left: auto;
    margin-right: auto;
    margin-bottom: 10px;
    font-size: 2.2rem;
  }
  .about li {
    font-size: 1.5rem;
    color: white;
    font-family: "Open Sans", sans-serif;
    text-align: center;
    list-style-position: inside;
    margin-bottom: 5px;
  }
  @media (max-width: 1100px) {
    .about-text {
      padding: 30px 40px;
    }
  }
  @media (max-width: 900px) {
    #about {
      flex-direction: column;
      align-items: center;
      gap: 0;
    }
    .about-left, .about-right {
      width: 100%;
      max-width: 600px;
      padding: 20px 5vw;
      box-sizing: border-box;
    }
  }
  @media (max-width: 800px) {
    #about {
      flex-direction: column;
      align-items: center;
    }
    .about-text {
      padding: 20px 10vw;
      width: 100%;
      max-width: 600px;
    }
  }
  @media (max-width: 500px) {
    .about-text {
      padding: 10px 2vw;
    }
  }

  #carousel {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
  }
  #carousel h1 {
    color: #FF5F05;
    width: 100%;
    text-align: center;
    margin-bottom: 20px;
  }
  #carousel .carousel {
    position: relative;
    width: 80%;
    max-width: 900px;
    overflow: hidden;
    border-radius: 15px;
    margin: 40px auto;
    box-shadow: 0 6px 18px rgba(0,0,0,0.25);
    background: #fff;
    margin-top: 0px;
  }
  .carousel__viewport {
    display: flex;
    scroll-snap-type: x mandatory;
    overflow-x: scroll;
    scroll-behavior: smooth;
    -webkit-overflow-scrolling: touch;
    scrollbar-width: none;
    -ms-overflow-style: none;
  }
  .carousel__viewport::-webkit-scrollbar {
    display: none;
  }
  .carousel__slide {
    flex: 0 0 100%;
    scroll-snap-align: center;
    position: relative;
    text-align: center;
  }
  .carousel__slide img {
    width: 100%;
    height: 400px;
    object-fit: cover;
    border-bottom: 4px solid #13294B;
  }
  .carousel__slide p {
    font-size: 1.5rem;
    padding: 10px;
    color: #13294B;
    font-weight: bold;
  }
  .carousel__navigation {
    position: absolute;
    top: 50%;
    width: 100%;
    display: flex;
    justify-content: space-between;
    transform: translateY(-50%);
    pointer-events: none;
  }
  .carousel__navigation button {
    background: rgba(0,0,0,0.5);
    border: none;
    color: white;
    font-size: 2rem;
    padding: 10px 15px;
    border-radius: 50%;
    cursor: pointer;
    pointer-events: all;
    transition: background 0.3s ease;
  }
  #carousel .carousel__dots {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 12px;
    margin: 0px 0 20px 0;
    position: relative;
    z-index: 2;
  }
  .carousel__dot {
    width: 18px;
    height: 18px;
    border-radius: 50%;
    background: #e0e0e0;
    border: 2px solid #FF5F05;
    display: inline-block;
    transition: background 0.3s, transform 0.3s, border 0.3s;
    cursor: pointer;
    box-shadow: 0 1px 4px rgba(0,0,0,0.10);
  }
  .carousel__dot.active {
    background: #FF5F05;
    transform: scale(1.2);
  }

  #favs {
    padding: 2.5rem 0 2.5rem 0;
    background: #FF5F05;
    width: 100%;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    align-items: center;
    min-height: 0;
  }
  #favs h2 {
    font-size: 1.7rem;
    margin-bottom: 20px;
  }
  #favs p {
    font-size: 1rem;
    color: black;
  }
  .favs-grid {
    display: grid;
    grid-template-columns: repeat(3, minmax(0, 1fr));
    gap: 1rem 1rem;
    width: 100vw;
    max-width: 1100px;
    margin: 0 auto;
    box-sizing: border-box;
  }
  .fav-card {
    background: #fff;
    border-radius: 1rem;
    overflow: hidden;
    box-shadow: 0 4px 8px rgba(0,0,0,0.1);
    cursor: pointer;
    transition: transform 0.2s ease;
    width: 90%;
    min-width: 0;
    height: 90%;
    margin: 0 auto;
    display: flex;
    flex-direction: column;
    align-items: center;
  }
  .fav-card:hover {
    transform: translateY(-5px);
  }
  .fav-card img {
    width: 100%;
    height: 150px;
    object-fit: cover;
    display: block;
  }
  .fav-card .card-info {
    padding: 0.5rem;
    text-align: center;
  }
  .fav-card .card-info h2 {
    font-size: 1.4rem !important;
    margin: 0.2rem 0 0.1rem 0;
    word-break: break-word;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
  }
  @media (max-width: 900px) {
    .favs-grid {
      grid-template-columns: repeat(2, minmax(0, 1fr));
    }
  }
  @media (max-width: 600px) {
    .favs-grid {
      grid-template-columns: 1fr;
      gap: 1rem;
    }
    .fav-card {
      max-width: 95vw;
      width: 98%;
    }
  }

  .modal {
    display: none;
    position: fixed;
    z-index: 1000;
    inset: 0;
    background: rgba(0,0,0,0.6);
    justify-content: center;
    align-items: center;
  }
  .modal-content {
    background: #fff;
    border-radius: 1rem;
    padding: 1.5rem;
    max-width: 500px;
    width: 90%;
    text-align: center;
    overflow-y: auto;
    max-height: 90vh;
    color: rgb(0, 0, 0);
  }
  .modal-content img {
    width: 100%;
    border-radius: 0.5rem;
  }
  .close {
    position: absolute;
    top: 1rem;
    right: 1.5rem;
    font-size: 1.5rem;
    cursor: pointer;
  }

  #contact {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    padding: 40px 0;
    background: #fff;
    text-align: center;
  }
  .contact-container {
    display: flex;
    width: 80vw;
    max-width: 1200px;
    min-height: 60vh;
    margin: 0 auto;
    background: none;
    box-shadow: none;
  }
  .contact-left {
    flex: 1 1 0;
    background: none;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: flex-start;
    padding: 40px;
    min-width: 0;
    max-width: 350px;
    text-align: left;
    margin-right: 20px;
  }
  .contact-left h1 {
    color: #13294B;
    margin-bottom: 18px;
    font-size: 2.5rem;
  }
  .contact-text p {
    color: #13294B;
    font-size: 1.1rem;
    margin-bottom: 8px;
    justify-content: center;
  }
  .contact-email {
    font-weight: bold;
    color: #FF5F05;
    font-size: 1.1rem;
    margin-top: 18px;
    margin-bottom: 10px;
    word-break: break-all;
  }
  .contact-socials {
    margin-top: 18px;
    display: flex;
    justify-content: center;
    gap: 18px;
  }
  .contact-socials a {
    color: #13294B !important;
    font-size: 2rem;
    transition: color 0.2s;
  }
  .contact-socials a:hover {
    color: #FF5F05 !important;
  }
  .contact-right {
    flex: 2 1 0;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 40px 0 40px 32px;
    min-width: 0;
    margin-left: 20px;
  }
  .contact-video-label {
    color: #13294B;
    font-size: 1.3rem;
    font-weight: 600;
    margin-bottom: 18px;
    text-align: center;
  }
  .contact-video {
    width: 100%;
    max-width: 600px;
    min-width: 240px;
    height: 340px;
    background: #000;
    border-radius: 12px;
    box-shadow: 0 2px 12px rgba(0,0,0,0.12);
    object-fit: cover;
  }
  @media (max-width: 900px) {
    .contact-container {
      flex-direction: column;
      width: 98vw;
      min-height: unset;
      max-width: 98vw;
    }
    .contact-left, .contact-right {
      padding: 24px 0 0 0;
      max-width: 100vw;
      align-items: center;
      text-align: center;
    }
    .contact-right {
      padding: 0 0 24px 0;
    }
    .contact-video {
      width: 90vw;
      max-width: 98vw;
      height: 220px;
    }
  }
  #contact video {
    display: block;
    margin: 0 auto;
    max-width: 100%;
    height: auto;
    background: #000;
    border-radius: 12px;
    box-shadow: 0 2px 12px rgba(0,0,0,0.12);
  }
  #contact h1 {
    color: #13294B;
  }
  #contact p {
    font-size: 1.5rem;
    color: #13294B;
    font-family: "Open Sans", sans-serif;
    text-align: center;
  }

  h1 {
    font-size: 3.5rem;
    letter-spacing: 2px;
    text-align: center;
    color: white;
  }
  p {
    text-align: center;
    margin-bottom: 2px;
  }

